    echo -e "${NC}"
}

print_status() {
    echo -e "${GREEN}✅ $1${NC}"
}

print_info() {
    echo -e "${BLUE}ℹ️  $1${NC}"
}

print_warning() {
    echo -e "${YELLOW}⚠️  $1${NC}"
}

print_error() {
    echo -e "${RED}❌ $1${NC}"
}

# Test variables
COMPATIBILITY_SCORE=0
TOTAL_TESTS=0
PASSED_TESTS=0

# Use the correct upid command
if command -v ./upid &> /dev/null; then
    UPID_CMD="./upid"
elif command -v upid &> /dev/null; then
    UPID_CMD="upid"
else
    echo "❌ UPID binary not found. Please build it first with: python build_binary.py"
    exit 1
fi

test_basic_kubectl_compatibility() {
    print_section "BASIC kubectl COMPATIBILITY TEST"
    
    local test_count=0
    local pass_count=0
    
    # Test 1: If kubectl cluster-info works, upid cluster info should work
    print_info "Test 1: kubectl cluster-info → upid cluster info"
    ((test_count++))
    if kubectl cluster-info &> /dev/null; then
        print_status "kubectl cluster-info: SUCCESS"
        if timeout 30 $UPID_CMD cluster info &> /dev/null; then
            print_status "upid cluster info: SUCCESS"
            ((pass_count++))
        else
            print_warning "upid cluster info: TIMEOUT/ERROR"
        fi
    else
        print_error "kubectl cluster-info: FAILED - Cannot test UPID compatibility"
        return 1
    fi
    
    # Test 2: If kubectl get nodes works, upid should detect cluster
    print_info "Test 2: kubectl get nodes → upid cluster detection"
    ((test_count++))
    if kubectl get nodes &> /dev/null; then
        print_status "kubectl get nodes: SUCCESS"
        if timeout 30 $UPID_CMD analyze cluster default --format table &> /dev/null; then
            print_status "upid analyze cluster: SUCCESS"
            ((pass_count++))
        else
            print_warning "upid analyze cluster: TIMEOUT/ERROR"
        fi
    else
        print_error "kubectl get nodes: FAILED"
    fi
    
    # Test 3: If kubectl config current-context works, upid auth should work  
    print_info "Test 3: kubectl config current-context → upid auth status"
    ((test_count++))
    if kubectl config current-context &> /dev/null; then
        print_status "kubectl config current-context: SUCCESS"
        if timeout 30 $UPID_CMD auth status &> /dev/null; then
            print_status "upid auth status: SUCCESS"
            ((pass_count++))
        else
            print_warning "upid auth status: TIMEOUT/ERROR"
        fi
    else
        print_error "kubectl config current-context: FAILED"
    fi
    
    # Test 4: If kubectl get pods works, upid should analyze resources
    print_info "Test 4: kubectl get pods → upid analyze resources"
    ((test_count++))
    if kubectl get pods --all-namespaces &> /dev/null; then
        print_status "kubectl get pods: SUCCESS"
        if timeout 30 $UPID_CMD analyze resources default --format table &> /dev/null; then
            print_status "upid analyze resources: SUCCESS"
            ((pass_count++))
        else
            print_warning "upid analyze resources: TIMEOUT/ERROR"
        fi
    else
        print_error "kubectl get pods: FAILED"
    fi
    
    TOTAL_TESTS=$((TOTAL_TESTS + test_count))
    PASSED_TESTS=$((PASSED_TESTS + pass_count))
    
    local score=$((pass_count * 100 / test_count))
    print_info "Basic Compatibility Score: $pass_count/$test_count ($score%)"
    
    return $score
}

test_cluster_type_detection() {
    print_section "CLUSTER TYPE DETECTION TEST"
    
    local test_count=1
    local pass_count=0
    
    print_info "Testing universal cluster type detection..."
    
    # Get current cluster info
    local kubectl_context=$(kubectl config current-context 2>/dev/null || echo "unknown")
    local kubectl_cluster=$(kubectl cluster-info 2>/dev/null | head -1 || echo "unknown")
    
    print_info "Current kubectl context: $kubectl_context"
    print_info "Current kubectl cluster: $kubectl_cluster"
    
    # Test UPID cluster detection
    if timeout 30 $UPID_CMD cluster info > /tmp/upid_cluster_test.log 2>&1; then
        local upid_cluster_type=$(grep -i "type\|kind\|distribution" /tmp/upid_cluster_test.log || echo "detected")
        print_status "UPID cluster detection: SUCCESS"
        print_info "UPID detected: $upid_cluster_type"
        ((pass_count++))
    else
        print_warning "UPID cluster detection: FAILED"
        cat /tmp/upid_cluster_test.log 2>/dev/null || true
    fi
    
    TOTAL_TESTS=$((TOTAL_TESTS + test_count))
    PASSED_TESTS=$((PASSED_TESTS + pass_count))
    
    rm -f /tmp/upid_cluster_test.log
    
    local score=$((pass_count * 100 / test_count))
    print_info "Cluster Detection Score: $pass_count/$test_count ($score%)"
    
    return $score
}

test_namespace_compatibility() {
    print_section "NAMESPACE COMPATIBILITY TEST"
    
    local test_count=0
    local pass_count=0
    
    # Get all namespaces that kubectl can see
    local namespaces=($(kubectl get namespaces -o name 2>/dev/null | sed 's/namespace\///'))
    
    if [ ${#namespaces[@]} -eq 0 ]; then
        print_warning "No namespaces found with kubectl"
        return 50
    fi
    
    print_info "Found ${#namespaces[@]} namespaces with kubectl"
    
    # Test first 3 namespaces (to avoid timeout)
    local test_namespaces=("${namespaces[@]:0:3}")
    
    for ns in "${test_namespaces[@]}"; do
        print_info "Testing namespace: $ns"
        ((test_count++))
        
        # If kubectl can list pods in this namespace, UPID should be able to analyze it
        if kubectl get pods -n "$ns" &> /dev/null; then
            if timeout 20 $UPID_CMD analyze resources "$ns" --format table &> /dev/null; then
                print_status "UPID analysis for $ns: SUCCESS"
                ((pass_count++))
            else
                print_warning "UPID analysis for $ns: TIMEOUT/ERROR"
            fi
        else
            print_warning "kubectl access to $ns: LIMITED"
        fi
    done
    
    TOTAL_TESTS=$((TOTAL_TESTS + test_count))
    PASSED_TESTS=$((PASSED_TESTS + pass_count))
    
    local score=50
    if [ $test_count -gt 0 ]; then
        score=$((pass_count * 100 / test_count))
    fi
    
    print_info "Namespace Compatibility Score: $pass_count/$test_count ($score%)"
    
    return $score
}

test_error_handling() {
    print_section "ERROR HANDLING & EDGE CASES TEST"
    
    local test_count=0
    local pass_count=0
    
    # Test 1: Invalid namespace
    print_info "Test 1: Invalid namespace handling"
    ((test_count++))
    if timeout 15 $UPID_CMD analyze resources "nonexistent-namespace-12345" --format table &> /tmp/upid_error_test.log; then
        # Should handle gracefully, not crash
        if grep -i "not found\|error\|warning" /tmp/upid_error_test.log > /dev/null; then
            print_status "Graceful error handling: SUCCESS"
            ((pass_count++))
        else
            print_warning "Error handling could be improved"
        fi
    else
        print_warning "Command timeout or crash"
    fi
    
    # Test 2: Network timeout handling
    print_info "Test 2: Command timeout behavior"
    ((test_count++))
    # This should complete within reasonable time or timeout gracefully
    if timeout 10 $UPID_CMD --help &> /dev/null; then
        print_status "Help command response: SUCCESS"
        ((pass_count++))
    else 
        print_warning "Help command slow or failing"
    fi
    
    # Test 3: Version detection
    print_info "Test 3: Version detection"
    ((test_count++))
    if timeout 5 $UPID_CMD --version &> /dev/null; then
        local version_output=$($UPID_CMD --version 2>&1)
        print_status "Version detection: SUCCESS ($version_output)"
        ((pass_count++))
    else
        print_warning "Version detection: FAILED"
    fi
    
    TOTAL_TESTS=$((TOTAL_TESTS + test_count))
    PASSED_TESTS=$((PASSED_TESTS + pass_count))
    
    rm -f /tmp/upid_error_test.log
    
    local score=$((pass_count * 100 / test_count))
    print_info "Error Handling Score: $pass_count/$test_count ($score%)"
    
    return $score
}

test_core_features() {
    print_section "CORE FEATURES COMPATIBILITY TEST"
    
    local test_count=0
    local pass_count=0
    
    # Test key UPID features that should work if kubectl works
    local features=(
        "status"
        "auth status"  
        "cluster info"
        "analyze cluster default"
        "intelligence analyze default"
    )
    
    for feature in "${features[@]}"; do
        print_info "Testing: upid $feature"
        ((test_count++))
        
        if timeout 20 $UPID_CMD $feature &> /dev/null; then
            print_status "upid $feature: SUCCESS"
            ((pass_count++))
        else
            print_warning "upid $feature: TIMEOUT/ERROR"
        fi
    done
    
    TOTAL_TESTS=$((TOTAL_TESTS + test_count))
    PASSED_TESTS=$((PASSED_TESTS + pass_count))
    
    local score=$((pass_count * 100 / test_count))
    print_info "Core Features Score: $pass_count/$test_count ($score%)"
    
    return $score
}

generate_compatibility_report() {
    print_section "COMPATIBILITY REPORT GENERATION"
    
    local report_file="upid_kubectl_compatibility_$(date +%Y%m%d_%H%M%S).log"
    
    # Calculate overall score
    local overall_score=0
    if [ $TOTAL_TESTS -gt 0 ]; then
        overall_score=$((PASSED_TESTS * 100 / TOTAL_TESTS))
    fi
    
    {
        echo "UPID CLI - kubectl Compatibility Test Report"
        echo "Generated: $(date)"
        echo "============================================="
        echo
        echo "SUMMARY:"
        echo "  Total Tests: $TOTAL_TESTS"
        echo "  Passed Tests: $PASSED_TESTS"
        echo "  Overall Score: $overall_score%"
        echo
        echo "ENVIRONMENT:"
        echo "  kubectl version: $(kubectl version --client --short 2>/dev/null || echo 'Unknown')"
        echo "  Current context: $(kubectl config current-context 2>/dev/null || echo 'Unknown')"
        echo "  UPID version: $($UPID_CMD --version 2>/dev/null || echo 'Unknown')"
        echo "  Cluster access: $(kubectl cluster-info 2>/dev/null | head -1 || echo 'None')"
        echo
        echo "COMPATIBILITY ASSESSMENT:"
        if [ $overall_score -ge 80 ]; then
            echo "  Status: ✅ EXCELLENT - UPID fully compatible with kubectl"
            echo "  Confidence: High - Ready for customer deployment"
        elif [ $overall_score -ge 60 ]; then
            echo "  Status: ✅ GOOD - UPID mostly compatible with kubectl"  
            echo "  Confidence: Medium - Some features may need cluster setup"
        elif [ $overall_score -ge 40 ]; then
            echo "  Status: ⚠️  PARTIAL - UPID partially compatible"
            echo "  Confidence: Low - Check cluster configuration"
        else
            echo "  Status: ❌ POOR - Compatibility issues detected"
            echo "  Confidence: None - Check UPID installation"
        fi
        echo
        echo "CONCLUSION:"
        echo "  UPID CLI universal compatibility: $overall_score%"
        echo "  Customer deployment readiness: $([ $overall_score -ge 70 ] && echo 'READY' || echo 'NEEDS WORK')"
        
    } > $report_file
    
    print_status "Compatibility report saved: $report_file"
    
    # Display key results
    echo
    if [ $overall_score -ge 80 ]; then
        print_status "🎉 EXCELLENT COMPATIBILITY - Ready for customer deployment!"
    elif [ $overall_score -ge 60 ]; then
        print_status "✅ GOOD COMPATIBILITY - Mostly ready for deployment"
    else
        print_warning "⚠️  COMPATIBILITY ISSUES - Further testing needed"
    fi
    
    echo -e "${CYAN}Overall kubectl compatibility: $overall_score% ($PASSED_TESTS/$TOTAL_TESTS tests passed)${NC}"
}

main() {
    print_header
    
    print_info "Testing UPID CLI compatibility with kubectl across all scenarios..."
    print_info "Principle: If kubectl works, UPID should work"
    echo
    
    # Run all compatibility tests
    test_basic_kubectl_compatibility
    test_cluster_type_detection
    test_namespace_compatibility  
    test_error_handling
    test_core_features
    
    # Generate final report
    generate_compatibility_report
    
    # Final assessment
    local overall_score=$((PASSED_TESTS * 100 / TOTAL_TESTS))
    
